function button() 
{

  var id = document.getElementById('proyecto').value;
  if (id == '1') {

		window.open('https://sites.google.com/a/gruposese.com/calidad-prl/4-gestion-de-incidencias/transporte', '_blank');

  }
  if (id=='2') {

	  window.open('https://sites.google.com/a/gruposese.com/calidad-prl/4-gestion-de-incidencias/alagon', '_blank');

  }
  if (id=='3') {

		window.open('https://sites.google.com/a/gruposese.com/calidad-prl/4-gestion-de-incidencias/logistica', '_blank');

  }
  if (id=='4') {

		window.open('https://sites.google.com/a/gruposese.com/calidad-prl/4-gestion-de-incidencias/serv-industriales', '_blank');

  }
  if (id=='5') {

		window.open('https://sites.google.com/a/gruposese.com/calidad-prl/4-gestion-de-incidencias/bonavia', '_blank');

  }
  }
